components.custom:
let formInstance = instance.root;
formInstance.showErrors([]);

const formData = submission.data;
if (!formData || !formData.sourceId) {
    formInstance.showErrors("Вы должны указать sourceId");
}

let sourceDaoId = "source";
let journalSourceId = formData.sourceId;
const sourceIdSlashIdx = journalSourceId.indexOf('/');
if (sourceIdSlashIdx > 0) {
    sourceDaoId = journalSourceId.substring(0, sourceIdSlashIdx) + "/" + sourceDaoId;
    journalSourceId = journalSourceId.substring(sourceIdSlashIdx + 1);
}
let sourceInfoId = Citeck.Records.get(sourceDaoId + '@' + journalSourceId);
Citeck.Records.get(sourceInfoId).load('columnsSourceId').then(columnsSourceId => {

    if (!columnsSourceId) {
        formInstance.showErrors("Для данного источника данных не настроен источник с колонками.")
        return;
    }

    const currentColumnsSet = {};
    const currentColumns = _.get(submission, 'data.columns', [])
    for (let column of currentColumns) {
        currentColumnsSet[column.name || ''] = true;
    }

    const applyAutoColumns = (columns) => {

        columns = columns.filter(c => !c.name || !currentColumnsSet[c.name]);

        if (columns.length) {
            submission = {
                ...submission,
                data: {
                    ...submission.data,
                    columns: [
                        ...submission.data.columns,
                        ...columns
                    ]
                }
            }
            formInstance.submission = submission;
        }
    }

    Citeck.Dialogs.showFormDialog({
        title: 'Выбор колонок',
        showDefaultButtons: true,
        formDefinition: {
            components: [
                {
                    label: 'Колонки',
                    key: 'selected-columns',
                    type: "selectJournal",
                    queryData: submission,
                    customSourceId: columnsSourceId,
                    journalId: 'journal-columns',
                    multiple: true
                }
            ]
        },
        onSubmit: submission => {
            let columns = submission.data['selected-columns'] || [];
            console.log('selected columns', columns);
            Citeck.Records.get(columns)
                .load({label: 'label{ru,en}', name: 'name'})
                .then(applyAutoColumns)
                .catch(e => formInstance.showErrors(e));
        }
    });

}).catch(() => formInstance.showErrors("Ошибка при загрузке информации по источнику данных"));
