components.custom:
let formInstance = instance.root;
formInstance.showErrors([]);

if (!data.auto_columns_url) {
    formInstance.showErrors(new Error("Укажите пожалуйста URL для загрузки колонок"), true);
    return;
}
if (!data.auto_columns_method) {
    formInstance.showErrors(new Error("Укажите пожалуйста метод для загрузки колонок (GET или POST)"), true);
    return;
}

const currentColumnsSet = {};
const currentColumns = _.get(submission, 'data.columns', [])
for (let column of currentColumns) {
    currentColumnsSet[column.name || ''] = true;
}

const applyAutoColumns = (columns) => {

    columns = columns.filter(c => !c.name || !currentColumnsSet[c.name]);

    if (columns.length) {
        submission = {
            ...submission,
            data: {
                ...submission.data,
                columns: [
                    ...submission.data.columns,
                    ...columns
                ]
            }
        }
        formInstance.submission = submission;
    }
}

const queryOptions = {
    method: data.auto_columns_method
};
if (queryOptions.method === "POST") {
    queryOptions.body = submission;
}

ecosFetch(data.auto_columns_url, queryOptions).then(res => {
    if (res.ok) {
        res.json().then(json => {
            if (!json.columns) {
                formInstance.showErrors(new Error("Сервер не вернул список колонок"), true);
            } else {
                applyAutoColumns(json.columns);
            }
        })
    } else {
        let msg = res.message || res.statusText;
        msg = msg ? ": " + msg : "";
        formInstance.showErrors(new Error("Произошла ошибка при загрузке колонок" + msg), true);
    }
});
